mapper.select:
select
commentId, problemId, commentContent, userId, parentId, commentDatetime, commentLikeNumber
from leetcode.problemcomment


        and commentId = #{commentid}


        and problemId = #{problemid}


        and commentContent = #{commentcontent}


        and userId = #{userid}


        and parentId = #{parentid}


        and commentDatetime = #{commentdatetime}


        and commentLikeNumber = #{commentlikenumber}
