mapper.select:
select
problemId, problemName, solutionNumber, passRate, difficulty, occurrenceRate
from leetcode.problem


        and problemId = #{problemid}


        and problemName = #{problemname}


        and solutionNumber = #{solutionnumber}


        and passRate = #{passrate}


        and difficulty = #{difficulty}


        and occurrenceRate = #{occurrencerate}
